    public static String integerToRoman(int value) {
        if (value > 3999 || value < 1) throw new IllegalArgumentException();

        StringBuilder numeral = new StringBuilder();
        int i = 0;
        while (value > 0) {
            if (value - values[i] >= 0) {
                numeral.append(numerals[i]);
                value -= values[i];
            } else {
                i++;
            }
        }

        return numeral.toString();
    }

