import java.util.Arrays;
    public static int oneMissing(int[] arr) {
        int totalXor = 0;
        int arrXor = 0;
        for (int i = 1; i <= arr.length + 1; i++) totalXor ^= i;
        for (int i : arr) arrXor ^= i;
        return totalXor ^ arrXor;
    }
    public static int[] twoMissing(int[] arr) {
        int size = arr.length + 2;
        long totalSum = size * (size + 1) / 2;
        long arrSum = 0;
        for (int i : arr) arrSum += i;
        int pivot = (int) ((totalSum - arrSum) / 2);
        int totalLeftXor = 0;
        int arrLeftXor = 0;
        int totalRightXor = 0;
        int arrRightXor = 0;

        for (int i = 1; i <= pivot; i++) totalLeftXor ^= i;
        for (int i = pivot + 1; i <= size; i++) totalRightXor ^= i;
        for (int i : arr) {
            if (i <= pivot) arrLeftXor ^= i;
            else arrRightXor ^= i;
        }

        return new int[]{totalLeftXor ^ arrLeftXor, 
            totalRightXor ^ arrRightXor};
    }

import java.util.HashSet;
    public static int consecutive(int[] a) {
        HashSet<Integer> values = new HashSet();
        for (int i : a) {
            values.add(i);
        }
        int maxLength = 0;
        for (int i : values) {
            if (values.contains(i - 1)) continue;
            int length = 0;
            while (values.contains(i++)) length++;
            maxLength = Math.max(maxLength, length);
        }

        return maxLength;
    }

private static final int BITS_IN_INTEGER = 32;
public static int rotate(int x, int N) {
    N = N % BITS_IN_INTEGER;
    return (x >> N | x << (BITS_IN_INTEGER - N));
}

public static int sum(int a, int b) {
    if (b == 0){
        return a;
    }
    int partialSum = a ^ b;
    int carry = (a & b) << 1;
    return sum(partialSum, carry);
}
